select a.*,b.USER_NAME from g_sys_manage a left join g_sys_user b on a.USER_ID = b.USER_ID where 1=1 and a.RELEASE_STATUS = '1' and a.EXAMINE_STATUS = '1' and a.IS_VALID = '1'
and a.TYPE = #{type,jdbcType=CHAR}
and a.TITLE like concat('%','${searchContent}','%') or a.CONTENT like concat('%','${searchContent}','%')

select
  a.*,b.USER_NAME
from g_sys_manage a left join g_sys_user b on a.USER_ID = b.USER_ID
where a.OBJ_ID = #{objId,jdbcType=BIGINT}

  and a.TYPE = #{type,jdbcType=CHAR}
